mapper.select:
        SELECT


--         级联表的字段
        ,goods.goods_name as goodsName
        ,goods.goods_types as goodsTypes
        ,goods.goods_photo as goodsPhoto
        ,goods.goods_kucun_number as goodsKucunNumber
        ,goods.goods_old_money as goodsOldMoney
        ,goods.goods_new_money as goodsNewMoney
        ,goods.goods_clicknum as goodsClicknum
        ,goods.shangxia_types as shangxiaTypes
        ,goods.goods_delete as goodsDelete
        ,goods.goods_content as goodsContent
        ,yonghu.yonghu_name as yonghuName
        ,yonghu.yonghu_photo as yonghuPhoto
        ,yonghu.yonghu_phone as yonghuPhone
        ,yonghu.yonghu_id_number as yonghuIdNumber
        ,yonghu.new_money as newMoney
        ,yonghu.yonghu_delete as yonghuDelete

        FROM goods_commentback  a
        left JOIN goods goods ON a.goods_id = goods.id
        left JOIN yonghu yonghu ON a.yonghu_id = yonghu.id



                and a.id in

                #{item}



                and a.goods_id = #{params.goodsId}


                and a.yonghu_id = #{params.yonghuId}


                and a.goods_commentback_text like CONCAT('%',#{params.goodsCommentbackText},'%')


                and a.reply_text like CONCAT('%',#{params.replyText},'%')


                  and UNIX_TIMESTAMP(a.insert_time) >= UNIX_TIMESTAMP(#{params.insertTimeStart}) 


                  and UNIX_TIMESTAMP(a.insert_time) <= UNIX_TIMESTAMP(#{params.insertTimeEnd}) 


                  and UNIX_TIMESTAMP(a.update_time) >= UNIX_TIMESTAMP(#{params.updateTimeStart}) 


                  and UNIX_TIMESTAMP(a.update_time) <= UNIX_TIMESTAMP(#{params.updateTimeEnd}) 




                and a.goods_id IS NOT NULL


                and goods.goods_name like CONCAT('%',#{params.goodsName},'%')


                and goods.goods_types = #{params.goodsTypes}



                  and goods.goods_kucun_number >= #{params.goodsKucunNumberStart}   


                  and goods.goods_kucun_number <= #{params.goodsKucunNumberEnd}   


                and goods.goods_kucun_number = #{params.goodsKucunNumber}


                  and goods.goods_old_money >= #{params.goodsOldMoneyStart}   


                  and goods.goods_old_money <= #{params.goodsOldMoneyEnd}   


                  and goods.goods_new_money >= #{params.goodsNewMoneyStart}   


                  and goods.goods_new_money <= #{params.goodsNewMoneyEnd}   


                  and goods.goods_clicknum >= #{params.goodsClicknumStart}   


                  and goods.goods_clicknum <= #{params.goodsClicknumEnd}   


                and goods.goods_clicknum = #{params.goodsClicknum}


                and goods.shangxia_types = #{params.shangxiaTypes}



                  and goods.goods_delete >= #{params.goodsDeleteStart}   


                  and goods.goods_delete <= #{params.goodsDeleteEnd}   


                and goods.goods_delete = #{params.goodsDelete}


                and goods.goods_content like CONCAT('%',#{params.goodsContent},'%')



                and a.yonghu_id IS NOT NULL


                and yonghu.yonghu_name like CONCAT('%',#{params.yonghuName},'%')


                and yonghu.yonghu_phone like CONCAT('%',#{params.yonghuPhone},'%')


                and yonghu.yonghu_id_number like CONCAT('%',#{params.yonghuIdNumber},'%')


                  and yonghu.new_money >= #{params.newMoneyStart}   


                  and yonghu.new_money <= #{params.newMoneyEnd}   


                  and yonghu.yonghu_delete >= #{params.yonghuDeleteStart}   


                  and yonghu.yonghu_delete <= #{params.yonghuDeleteEnd}   


                and yonghu.yonghu_delete = #{params.yonghuDelete}



        order by a.${params.orderBy} desc
